import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../contexts/authContext";
import "./login.css";

// `https://git.heroku.com/smart-dream-journal-frontend.git/profile/token`,

async function getAccessToken(credentials) {
  return fetch(
    "https://smart-dream-journal-backend-bf5b1011b117.herokuapp.com/token",
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(credentials),
    }
  )
    .then((response) => {
      if (response.status === 200) {
        return response.json();
      }
    })
    .then((data) => {
      return data.access_token;
    })
    .catch((error) => console.log(error));
}

function Login() {
  const navigate = useNavigate();
  // states
  const [username, setUsername] = useState();
  const [password, setPassword] = useState();
  // contexts
  const { setCurrentUserName, setAccessToken, setTokenHeader } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    // fetch accessToken
    const accessToken = await getAccessToken({
      username,
      password,
    });
    // store access token, token header, and user name contexts
    localStorage.setItem("ACCESSTOKEN", accessToken);
    localStorage.setItem(
      "TOKENHEADER",
      JSON.stringify({ Authorization: `Bearer ${accessToken}` })
    );
    localStorage.setItem("USERNAME", username);
    setCurrentUserName(username);
    setAccessToken(accessToken);
    setTokenHeader({ Authorization: `Bearer ${accessToken}` });
    if (accessToken) {
      navigate("/profile");
    }
  };

  return (
    <div className="login">
      <div className="container">
        <div className="row align-items-center my-5">
          <div className="col-lg-7"></div>
          <div className="col-lg-5">
            <h1>Welcome to Smart Dream Journal!</h1>
            <h2 className="font-weight-light">Please log in</h2>
            <form onSubmit={handleSubmit}>
              <section>
                <label htmlFor="username">Username: </label>
                <input
                  type="text"
                  placeholder="username"
                  onChange={(e) => setUsername(e.target.value)}
                />
              </section>
              <section>
                <label htmlFor="password">Password: </label>
                <input
                  type="password"
                  placeholder="password"
                  onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit" className="login-button">
                  Login
                </button>
              </section>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
